import React, { useState } from "react";
import Sidebar from "../Components/SideNav";
import { CDBIcon, CDBSelect } from "cdbreact";
import Calendar from "react-calendar";
import { Link } from "react-router-dom";
import Pagefooter from "../Components/Pagefooter";

export default function EventList() {
  const [value, onChange] = useState(new Date());
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(3);
  const [pageNumberLimit] = useState(3);
  const [maxpageNumberLimit, setmaxpageNumberLimit] = useState(3);
  const [minpageNumberLimit, setminpageNumberLimit] = useState(0);

  //Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const tableData = [
    {
      id: 1,
      date: "3 Dec, 2022",
      time: "10: 15 AM",
      location: "king West Village",
    },
    {
      id: 2,
      date: "5 Dec, 2022",
      time: "11: 15 PM",
      location: "king East Village",
    },
    {
      id: 3,
      date: "7 Dec, 2022",
      time: "12: 15 PM",
      location: "king South Village",
    },
    {
      id: 4,
      date: "8 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 5,
      date: "8 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 6,
      date: "8 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 7,
      date: "32 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 8,
      date: "29 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 9,
      date: "30 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
    {
      id: 10,
      date: "30 Dec, 2022",
      time: "1: 15 PM",
      location: "king Nouth Village",
    },
  ];
  const [option] = useState([
    {
      text: "Mobile Computer Lab",
      value: "1",
    },
    {
      text: "Option 2",
      value: "2",
    },
    {
      text: "Option 3",
      value: "3",
    },
  ]);
  const currentPosts = tableData.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const NextPage = () => {
    setCurrentPage(currentPage + 1);
    if (currentPage + 1 > maxpageNumberLimit) {
      setmaxpageNumberLimit(maxpageNumberLimit + pageNumberLimit);
      setminpageNumberLimit(minpageNumberLimit + pageNumberLimit);
    }
  };

  return (
    <>
      <Sidebar />
      <body
