                <CDBSelect
                  options={option}
                  selected="Choose an Option"
                  style={{
                    width: "100%",
                    backgroundColor: "#028B2E",
                    color: "#fff",
                    fontWeight: "bold",
                  }}
                />
              </span>
            </div>
          </div>
        </div>
        <div className="row">
          <div
            className="col-11  col-lg-3  col-md-10   bg-light pt-2  mb-5 mt-3"
