import React, { useState } from "react";
import { Line } from 'react-chartjs-2';
import { CDBContainer ,CDBCircularProgress} from "cdbreact";
import Template from '../Images/BG.png'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js'




export const Progress = () => {
  return (
    <CDBContainer>
            
          <CDBCircularProgress

              value={47}
              max={100}
              min={0}
              color="info"
              text={`${47}%`}
            />
            
    </CDBContainer>
  );
};

export default function DashboardChart() {

  ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  )

  const [data] = useState({
    labels: ['10am', '11am', '12am', '1am', '2am', '3am', '4am', '5am', '6am', '7am'],
    datasets: [
      {
        label: 'My  dataset',
        backgroundColor: 'rgba(71, 225, 167, 0.5)',
        borderColor: 'rgb(71, 225, 167)',
        data: [0, 2, 4, 8, 10, 12, 14, 16, 18, 20]
      },
    ],
  });

  return (
    <div className="row " style={{marginTop: "2%" }}>
      <div className="col-11 col-md-7 bg-light m-3" style={{borderRadius: "15px"}}>
        <CDBContainer>
          <Line data={data} options={{ responsive: true }} />
        </CDBContainer>
      </div>
      <div className="col-11 col-md-4 bg-light m-2" style={{borderRadius: "15px"}}>
       <Progress/>
      </div>
    </div>
  );
}
